// import React, { useState } from "react";
import ReactDOM from "react-dom";

const App = () => {

  const [count, setCount] = React.useState(0);
  // let state = useState();

  const increase = () => {
    setCount(12);
  }

    return(
      <div className="container">
        <h1>{count}</h1>
        <button onClick={increase}>+</button>
      </div>
    )
}


export default App;
