import React from "react";
import ReactDOM from "react-dom";
import App from './components/App';

let count = 0;

let increase = () => {
  count++;
  ReactDOM.render(
    <div className="container">
      <h1>{count}</h1>
      <button onClick={increase}>+</button>
    </div>,
    document.getElementById("root")
  );

}

